\begin{align*}
\begin{matrix} {{ \chi _1} = [ - 100 \% , - 100 \% , - 100 \% ] , } \hfill \hfill \\ {{ \chi _2} = [ - 100 \% , - 100 \% , + 100 \% ] , } \hfill \\ {{ \chi _3} = [ - 100 \% , 100 \% , - 100 \% ] , } \hfill \hfill \\ {{ \chi _4} = [ - 100 \% , + 100 \% , + 100 \% ] , } \hfill \\ {{ \chi _5} = [ + 100 \% , - 100 \% , - 100 \% ] , } \hfill \hfill \\ {{ \chi _6} = [ + 100 \% , + 100 \% , + 100 \% ] , } \hfill \\ {{ \chi _7} = [ + 100 \% , - 100 \% , - 100 \% ] , } \hfill \hfill \\ {{ \chi _8} = [ + 100 \% , - 100 \% , + 100 \% ] , \, and} \hfill \\ {{ \chi _9} = [ 0 \% , 0 \% , 0 \% ] ;} \hfill & {} \hfill \\ {P ( { \chi _1} ) , \cdots , P ( { \chi _8} ) = 0.3;P ( { \chi _9} ) = 0.} \hfill & {} \hfill \\ \end{matrix}
